from downloadImages import getOutputMetadataFolder, getOutputMetadataPath,addFolderfrom s3 import upload_with_default_configurationimport osimport jsonbucket = '123nft'def getPathForS3(orderId, fileName):    return orderId + "/metadata/" + fileNamedef startGenerateMetadata(metadata, metadataFormat,orderId):    addFolder(getOutputMetadataFolder(orderId))        if(metadataFormat == 'Ethereum'):        generateJsonMetadata(metadata,orderId)    elif(metadataFormat == 'Cardano'):        generateJsonMetadata(metadata,orderId)    else:        generateJsonMetadata(metadata,orderId)          def generateJsonMetadata(metadata, orderId):    for metadataBlock in metadata:        fileName = metadataBlock['fileName'][:-4] # remove .png for now        jsonFileName = fileName + ".json"        outputMetadataFilePath = getOutputMetadataPath(jsonFileName,orderId)        f = open(outputMetadataFilePath,'w')        f.write(json.dumps(metadataBlock))        f.close()                  upload_with_default_configuration(outputMetadataFilePath,bucket,  getPathForS3(orderId, fileName),os.path.getsize(outputMetadataFilePath) )